const { core } = require('./core')

module.exports = {
  get_item: async (user_id) => {
    const response = await core.query('PK = :p_key AND SK = :s_key', {
      ':p_key': '#METADATA#USER',
      ':s_key': 'USER#' + user_id,
    })
    return response.Items ? response.Items[0] : {}
  },
}
